demoCode.js:
/* global Page */

/* eslint-disable babel/new-cap */
Page({
/* eslint-enable babel/new-cap */
    data: {
        codeImgList: [
            'https://b.bdstatic.com/searchbox/icms/searchbox/img/smt_ui_carptcha_1.png',
            'https://b.bdstatic.com/searchbox/icms/searchbox/img/smt_ui_carptcha_2.png',
            'https://b.bdstatic.com/searchbox/icms/searchbox/img/smt_ui_carptcha_3.png',
            'https://b.bdstatic.com/searchbox/icms/searchbox/img/smt_ui_carptcha_4.png',
            ''
        ],
        list: [
            {
                prevNum: 1,
                valueData: '',
                title: '默认样式',
                label: '验证码',
                labelWidth: '4em',
                placeholder: '请输入验证码',
                value: '',
                maxlength: 4,
                verifyImgUrl: 'https://b.bdstatic.com/searchbox/icms/searchbox/img/smt_ui_carptcha_1.png',
                inputErrMsg: '',
                time: 500
            },
            {
                prevNum: 1,
                valueData: '',
                title: '刷新中样式',
                labelWidth: '4em',
                placeholder: '图形验证码刷新中',
                label: '验证码',
                value: '',
                maxlength: 4,
                verifyImgUrl: 'https://b.bdstatic.com/searchbox/icms/searchbox/img/smt_ui_carptcha_1.png',
                inputErrMsg: '',
                time: 500000
            },
            {
                prevNum: 1,
                valueData: '',
                title: '刷新样式',
                labelWidth: '4em',
                placeholder: '点击刷新图形验证码',
                label: '验证码',
                value: '',
                maxlength: 4,
                verifyImgUrl: 'https://b.bdstatic.com/searchbox/icms/searchbox/img/smt_ui_carptcha_1.png',
                inputErrMsg: '',
                time: 5
            },
            {
                prevNum: 1,
                valueData: '',
                title: '自定义样式',
                placeholder: '请输入验证码',
                labelWidth: '6em',
                label: '验证码',
                value: '',
                maxlength: 4,
                verifyImgUrl: 'https://b.bdstatic.com/searchbox/icms/searchbox/img/smt_ui_carptcha_1.png',
                inputErrMsg: '',
                time: 500
            }
        ],
        index: ''
    },

    onReady() {
        setTimeout(() => {
            this.selectComponent('#smt-captcha-1').refreshImg();
            this.selectComponent('#smt-captcha-2').refreshImg();
        }, 500);
    },

    /**
     * 模拟更新验证码图片
     *
     * @param {Event} e 事件详情
     */
    refreshImg(e) {
        const index = e.currentTarget.dataset.info;
        this.data.index = index;
        const time = this.data.list[index].time;
        if (index === 2) {
            e.reload();
            return;
        }
        setTimeout(() => {
            let num = this.data.list[index].prevNum ++;
            if (num > 5) {
                this.data.list[index].prevNum = 0;
            }
            this.data.list[index].verifyImgUrl = this.data.codeImgList[num];
            if (!this.data.list[index].verifyImgUrl) {
                e.reload();
            }
            this.setData('list', this.data.list);
        }, time);
    },

    /**
     * 验证码输入事件
     *
     * @param {Event} e 事件详情
     */
    bindInput(e) {
        const index = e.currentTarget.dataset.info;
        this.data.list[index].valueData = e.detail;
    },

    /**
     * 失焦事件
     *
     * @param {Event} e 事件详情
     */
    blurHld(e) {
        const index = e.currentTarget.dataset.info;
        const {iptValue, check} = this.data.list[index].valueData;
        if (check || !iptValue) {
            this.data.list[index].inputErrMsg = '';
        }
        else {
            this.data.list[index].inputErrMsg = '请输入正确的验证码';
        }
        this.setData('list', this.data.list);
    }
});
