/**
 * @file image-item
 * @author v_zhaoxinggang
 * @date 2020-07-14
 */

/* global swan */
Component({ // eslint-disable-line
    extrnalClasses: ['image-container-class', 'opacity-border-class', 'plate-img-class'],
    properties: {
        // 图片地址
        imageSrc: {
            type: String,
            value: ''
        },

        // 图片宽度
        imageWidth: {
            type: Number,
            value: 100
        },

        // 图片高度
        imageHeight: {
            type: Number,
            value: 100
        },

        // 图片圆角
        borderRadius: {
            type: Number,
            value: 3
        },

        // 是否带边框
        imageBorder: {
            type: Boolean,
            value: false
        }
    }
});
