/**
 * @file list-item
 * @author v_zhaoxinggang
 * @date 2020-08-06
 */

import {isIos} from './../../common/utils/index';

/* global swan, Component */

Component({ // eslint-disable-line
    externalClasses: [
        'smt-label',
        'smt-content',
        'smt-list-label',
        'smt-list-content',
        'smt-list-item-container'
    ],

    properties: {

        // 样式类型，single 单行、list 列表、multiple 左右两列布局。
        type: {
            type: String,
            value: 'single'
        },

        // 标题文案 type === list 时必填
        title: {
            type: String,
            value: ''
        },

        // 左侧标题文案
        label: {
            type: String,
            value: ''
        },

        // 右侧内容文案
        content: {
            type: String,
            value: ''
        },

        // 列表数据：数据示例`[{label: '', content: ''}]` 字段名可通过 listLabelName 和 listContentName 自定义
        list: {
            type: Array,
            value: []
        },

        // 自定义 list 渲染字段名 label
        listLabelName: {
            type: String,
            value: 'label'
        },

        // 自定义 list 渲染字段名 content
        listContentName: {
            type: String,
            value: 'content'
        },

        // 是否禁用
        disable: {
            type: Boolean,
            value: false
        },

        // 右侧内容展示行数
        lineClamp: {
            type: Number,
            value: -1
        }
    },

    data: {
        isIos
    }
});
