/* global swan, Component */

import {isIos} from './../../common/utils/index';

Component({ // eslint-disable-line
    externalClasses: [
        'smt-list-container',
        'smt-title',
        'smt-sub-title',
        'smt-desc'
    ],

    properties: {

        // 主标题文案
        title: {
            type: String,
            value: ''
        },

        // 副标题文案
        subTitle: {
            type: String,
            value: ''
        },

        // 右侧辅助文案
        desc: {
            type: String,
            value: ''
        },

        // 图片链接
        imgSrc: {
            type: String,
            value: ''
        },

        // 是否有点击态
        disable: {
            type: Boolean,
            value: true
        }
    },

    data: {
        isIos
    }
});